  onDownload() {
    const resumeUrl = './assets/Aman_Tripathi_angular.pdf'; // Corrected file path
    const anchor = document.createElement('a'); // Create anchor element
    anchor.href = resumeUrl; // Set the file URL
    anchor.download = 'Aman_Tripathi_angular.pdf'; // Set the download file name
    anchor.target = '_blank'; // Optional: ensures the browser opens it in a new tab (if not downloading)
    document.body.appendChild(anchor); // Append to DOM for triggering click
    anchor.click(); // Trigger the download
    document.body.removeChild(anchor); // Remove the anchor from DOM
  }


  connectGitHub() {
    const gitHubUrl = 'https://github.com/aman73078';
    window.open(gitHubUrl, '_blank')
  }
